INSERT INTO `books` (`id`, `title`, `slug`, `image`, `state`, `createdAt`, `updatedAt`, `author`, `desc`, `translator`, `view`, `vip`) VALUES
(1, 'Ta Sư Phụ Mỗi Đến Đại Nạn Mới Đột Phá (Bản Dịch)', 'ta-su-phu-moi-den-dai-nan-moi-dot-pha-ban-dich', 'https://cdn.tienvuc.xyz/media/books/z3188572124149-ccc073a24c5eabb7192231610aff36ae-46bc.jpg', 'full', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Trư Mục 200 Cân', '<blockquote><p>Giảm 10% khi mua tối thiểu 500c</p></blockquote><p>\"Sư phụ, là tại đồ nhi vô năng, khiến người không đột phá được khi đại nạn tới.\"</p><p>\"Đồ nhi yên tâm, sư phụ không chết đượ', 'mocmeo', 0, 1),
(2, 'Ta Chỉ Muốn An Tĩnh Làm Người Trong Cẩu Đạo', 'ta-chi-muon-an-tinh-lam-nguoi-trong-cau-dao', 'https://cdn.tienvuc.xyz/media/books/ntcd-108f4.png', '', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Bạo Tạc Tiểu Nã Thiết', '<p><strong>#Ma_Đạo #Cẩu_Đạo #Siêu_Phẩm #Hệ_Thống #Hài_Hước #Logic</strong></p><p>“Chào mừng ngài sử dụng hệ thống tu chân toàn trí năng!”</p><p>“Ngài lựa chọn tu luyện Đoán Cốt Quyết, hệ thốn', 'vankhinhvota', 0, 1),
(3, 'Bàng Môn Đạo Sĩ Ở Thế Giới Chí Quái(Bản Dịch)', 'bang-mon-dao-si-o-the-gioi-chi-quai', 'https://cdn.tienvuc.xyz/media/books/b76c05c8-ed2b-4e95-95e9-a667130dabaa-3516ec.jpeg', 'full', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Thái Kiếm', '<p>Đây là một thế giới thần dị quỷ quái, lễ nghi bại hoại, đạo đức đổ nát. Nơi đây có kỳ nhân dị quỷ, lục thi yêu tiên, chi nhân thảo mã, tà tu chính thần. Đạo này có thi giải luyện khí, diên', 'hacthuytramchu', 0, 0),
(4, 'Đỉnh Cấp Khí Vận - Lặng Lẽ Tu Luyện Ngàn Năm', 'dinh-cap-khi-van-lang-le-tu-luyen-ngan-nam', 'https://cdn.tienvuc.xyz/media/books/bia-dckv-16ef.jpeg', 'full', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Nhâm Ngã Tiếu', '<p>Chuyển thế vào thế giới tu tiên, Hàn Tuyệt phát hiện mình mang theo thuộc tính trò chơi, vậy mà có thể đổ xúc xắc đổi mới linh căn tư chất và tiên thiên khí vận.</p><p>Kết quả là, hắn bỏ', 'vankhinhvota', 0, 1),
(5, 'Đánh Dấu Thành Thánh: Phát Hiện Lão Bà Lại Là Ma Đạo Nữ Đế (Dịch)', 'danh-dau-thanh-thanh-phat-hien-lao-ba-lai-la-ma-dao-nu-de-dich', 'https://cdn.tienvuc.xyz/media/books/z3431471206291-035779450a1523091ed4dd60008fa60a-c6a4cc.jpg', '', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Đồng Uyên', '<p>Hạ Hâm xuyên việt đến thế giới tu hành, ở bên trong một lần di tích rèn luyện cùng một nữ tử sinh ra tình cảm, có tiếp xúc da thịt, kết làm vợ chồng.<br><br>Sau khi ra di tích mới biết thê', 'mocmeo', 0, 0),
(6, 'Nhân Sinh Của Ta Có Thể Vô Hạn Mô Phỏng (Bản Dịch)', 'nhan-sinh-cua-ta-co-the-vo-han-mo-phong-ban-dich', 'https://cdn.tienvuc.xyz/media/books/z3278609243331-ef6f8954583d7f3fbe6650400b875aa6-848fb7.jpg', 'full', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Tân Đình', '<p>Cố Dương xuyên qua đến một cái thế giới cao võ, ở một cái thôn nhỏ sống tạm bợ hai năm, cuối cùng góp đủ khoản tiền thứ nhất, kích hoạt hệ thống, máy mô phỏng nhân sinh.</p><p>Tiêu tốn tiề', 'mocmeo', 0, 0),
(7, 'Võ Công Của Ta Quá Thần Kỳ, Có Thể Tự Động Tu Luyện', 'vo-cong-cua-ta-qua-than-ky-co-the-tu-dong-tu-luyen', 'https://cdn.tienvuc.vip/media/books/z3561226287092-8f6204b29fe878692e2a38d5016c8ba3-0ee93a.jpg', '', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Thần Kiến', '<blockquote><p>Giảm 10% khi mua tối thiểu 200 chương</p></blockquote>', 'mocmeo', 0, 0),
(8, 'Điên Rồi ! Ngươi Xác Định Ngươi Là Ngự Thú Sư? (Dịch)', 'dien-roi-nguoi-xac-dinh-nguoi-la-ngu-thu-su-dich', 'https://cdn.tienvuc.vip/media/books/z3530338748711-e837087dacf936122931f9bbfd67f087-c0b351.jpg', '', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Phẫn Nộ Bồ Đào', '<p>Tô Trạch xuyên tới thế giới vạn tộc, ở nơi này có đủ loại phương thức tu luyện.</p><p>Có võ đạo, có thuật sĩ, có ngự thú sư!</p><p>Trong nghi thức thức tỉnh, Tô Trạch thức tỉnh thiên phú n', 'mocmeo', 0, 1),
(9, 'Người Đọc Sách Đại Ngụy', 'dai-nguy-nguoi-doc-sach', 'https://cdn.tienvuc.xyz/media/books/244688837-4413560672067613-556078235356907494-n-a26a.jpg', '', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Thất Nguyệt Vị Thì', '<p>Vào năm Võ Xương&nbsp;tại vị.</p><p>Đại Ngụy vương triều, nữ đế đăng cơ, nhật nguyệt trên cao.</p><p>Nhưng mà, nữ đế đăng cơ, thiên hạ đại loạn, triều dã sục sôi, trong khoảng thời gian ng', 'tangtrancac', 0, 0),
(10, 'Ta Có Một Cái Quan Tài Hoàng Kim (Bản Dịch)', 'ta-co-mot-cai-quan-tai-hoang-kim-ban-dich', 'https://cdn.tienvuc.xyz/media/books/z3194678092946-e78795e98d93fea9a4aeb1b6268e1657-d1a6.jpg', 'full', '2022-06-30 14:15:02.907', '2022-06-30 14:15:02.907', 'Phong Nam Bắc', '<p>p/s: kiểu linh khí khôi phục chứ ko phải thuần đô thị đâu nhé, chém giết hắc ám lắm</p><p>\"Cảm giác phụ cận có một người vừa mới tử vong, tính danh Tô Bạch, thu hoạch được một điểm cơ bản ', 'mocmeo', 0, 0),
(12, 'Ta Thật Không Phải Cái Thế Cao Nhân (Dịch)', 'ta-that-khong-phai-cai-the-cao-nhan-dich', 'https://cdn.tienvuc.xyz/media/books/ta-that-khong-phai-cai-the-cao-nhan-218efa.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Tâm Cảnh Vô Cực', '<p>Cái gì? Gà ta nuôi chính là Phượng Hoàng thuần huyết?</p><p>Cái gì? Cá chép ta nuôi đều là Chân Long?</p><p>Lý Phàm: Không, không có khả năng, tại sao ta lại có thể là cường giả cái thế đư', 'snail', 0, 0),
(13, 'Ta! Thiên Mệnh Đại Nhân Vật Phản Phái (Dịch)', 'ta-thien-menh-trum-phan-dien-ta-thien-menh-dai-nhan-vat-phan-phai', 'https://cdn.tienvuc.xyz/media/books/1e5f34f4c5d4348a6dc5-2c4e.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Thiên Mệnh Phản Phái', '<p>Ta Thiên Mệnh Đại Nhân Vật Phản Phái - Ta Thiên Mệnh Trùm Phản Diện ( Dịch full)</p><p>Truyện hay cùng thể loại: Nhân vật phản diện đại kiêu hùng - Thiên Sách Giáo Uý ( bản dịch trên tienv', 'nganlanxinloiem', 0, 1),
(14, 'Thả Nữ Phù Thủy Kia Ra (Bản Dịch)', 'tha-nu-phu-thuy-kia-ra-ban-dich', 'https://cdn.tienvuc.xyz/media/books/buong-em-phu-thuy-kia-ra-412b.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Nhị Mục', '<blockquote><p>Giảm 10% khi mua tối thiểu 500c</p><p>Giảm 20% khi mua tối thiểu 1200c</p></blockquote><p>Truyện do nhóm Thánh Thiên Tiên Vực phụ trách<br>Trình Nham, trạch nam kỹ thuật cơ khí', 'mocmeo', 0, 0),
(15, 'Đạo Quân (Dịch Full)', 'dao-quan-ban-dich', 'https://cdn.tienvuc.xyz/media/books/12d3fe42-e8b6-4c81-8305-9f7aa58a0cd9-bb7b.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Dược Thiên Sầu', '<blockquote><p><strong><u>Khuyến mãi:</u></strong></p><p><strong>Giảm 10% khi mua từ 500 chương trở lên.</strong></p><p><strong>Giảm 15% khi mua từ 1000 chương trở lên.</strong></p><p><strong', 'xuanhoathunguyet', 0, 0),
(16, 'Hồng Hoang Quan Hệ Hộ (Bản Dịch)', 'hong-hoang-quan-he-ho-ban-dich', 'https://cdn.tienvuc.xyz/media/books/741a0f7b2a1ce342ba0d-ae7a.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Thanh Phong Tiểu Đạo Đồng', '<blockquote><p>Mua combo từ 200c giảm giá 10%</p><p>Từ 500c giảm giá 15%</p><p>Từ 1000c giảm giá 25%</p></blockquote><p>Bạch Cẩm trọng sinh về thời điểm trước khi Tam Thanh thành Thánh, được ', 'nganlanxinloiem', 0, 0),
(17, 'Mỗi Tuần Ta Có Một Nghề Mới', 'moi-tuan-ta-co-mot-nghe-moi', 'https://cdn.tienvuc.xyz/media/books/received-494336575125358-71038.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Vô Hữu', '<p>P/S: Mua từ 600c - 15%, 1200c - 20%.</p><p>Thể nghiệm tài xế DiDi, ban thưởng một chiếc xe&nbsp;Pagani Zonda.<br><br>Thể nghiệm giảng viên&nbsp;đại học, ban thưởng mười tòa nhà.<br><br>Thể', 'overlordking', 0, 1),
(18, 'Tôi Có Một Căn Phòng Khủng Bố', 'toi-co-mot-can-phong-khung-bo', 'https://cdn.tienvuc.xyz/media/books/canphong-4bc6.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Ngã Hội Tu Không Điều', '<blockquote><ul><li><p><strong>Giảm giá 10% khi mua tối thiểu 200 chương</strong></p></li><li><p><strong>Giảm giá 15% khi mua tối thiểu 500 chương</strong></p></li><li><p><strong>Giảm giá 20%', 'aqua01', 0, 0),
(19, 'Trùng Sinh Chi Ma Giáo Giáo Chủ (Côn Luân Ma Chủ) - Dịch full', 'con-luan-ma-chu', 'https://cdn.tienvuc.xyz/media/books/197450685-2033841986767214-4787717052721639394-n-bafe.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Phong Thất Nguyệt', '<p>Sở Hưu:<br>Nếu có chuyện gì giết một người không giải quyết được...<br>Thì ta giết một đống người<br><br>Có kèm giảm giá: <br>200 chương giảm 10<br>400 chương giảm 15<br>800 chương giảm 20', 'athox', 0, 0),
(20, 'Đại Phụng Đả Canh Nhân (Bản Dịch)', 'dai-phung-da-canh-nhan', 'https://cdn.tienvuc.xyz/media/books/khong-co-tieu-de11-20210415193528-ffcd.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Mại Báo Tiểu Lang Quân', '<p>Đại Phụng có Đả Canh Nhân. Đả canh nhân là ai? Đả canh nhân là những người gõ chiêng gõ mõ báo canh giờ thời xưa, cũng là những người sống cùng bóng đêm quan sát chuyện thiên hạ. Còn ở Đại', 'huntercd', 0, 0),
(21, 'Bắt Đầu Mười Lần Rút Sau Đó Vô Địch (Bản Dịch)', 'bat-dau-muoi-lan-rut-sau-do-vo-dich-ban-dich', 'https://cdn.tienvuc.xyz/media/books/07ed9319c9e608b851f7-3aad10.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Lưu Phong Tiếu', '<blockquote><p>Giảm 15% khi mua tối thiểu 500 chương</p><p>Giảm 20% khi mua tối thiểu 1000 chương</p><p>Giảm 25% khi mua tối thiểu 1500 chương</p></blockquote><p>Sở Cuồng Nhân mang theo auto ', 'mocmeo', 0, 1),
(22, 'Trọng Sinh Trở Thành Mạnh Nhất Vũ Trụ (Dịch)', 'trong-sinh-tro-thanh-manh-nhat-vu-tru-dich', 'https://cdn.tienvuc.xyz/media/books/trongsinh-83c5.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Cực Địa Phong Nhận', '<p><code>Mua trên 500 chương tặng mã giảm giá 10%</code></p><p><code>Mua trên 1000 chương tặng mã giảm giá 15%</code></p><p><code>Mua trên 2000 chương tặng mã giảm giá 20%</code></p><p>Thể Lo', 'ram0356', 0, 0),
(23, 'Bắt Đầu Làm Thần Hào Từ Sau Khi Thi Đại Học', 'bat-dau-lam-than-hao-tu-sau-khi-thi-dai-hoc', 'https://cdn.tienvuc.xyz/media/books/5c1ddffb-fabd-4312-99dc-9844a8b051ab-4f66.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Bạch Ma Hải', '<p>Tự động giảm giá 15% khi mua 500 chương, giảm 25% khi mua 1000 chương nha.</p><p>Mình nhận ủng hộ mỗi 200 xu sẽ bạo thêm 1 chương. Mong mọi người ủng hộ cho mình có động lực chăm chỉ ạ ^^<', 'snail', 0, 0),
(24, 'Vị Hôn Thê Của Ta Là Kiếm Thánh (Dịch)', 'vi-hon-the-cua-ta-la-kiem-thanh', 'https://cdn.tienvuc.xyz/media/books/elidg0x-imgur-cdee.png', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Đích Thủy', '<blockquote><p>Mua 100 chương giảm&nbsp;<strong>10</strong>%, 200 chương giảm&nbsp;<strong>20</strong>%<br>5xu/chương</p></blockquote><p>Trần Mục xuyên việt tới Vạn Tượng đại lục, đánh dấu th', 'nvm1997', 0, 0),
(25, 'Nhân Vật Phản Diện Đại Kiêu Hùng (Dịch)', 'nhan-vat-phan-dien-dai-kieu-hung-dich', 'https://cdn.tienvuc.xyz/media/books/khong-co-tieu-de64-20210501224141-1018f.png', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Thiên Sách Giáo Úy', '<blockquote><p>Từ hiện tại đến hết tháng: ưu đãi từ chương 51-1000 còn 5x/chương, từ 1001 trở đi còn 8x/chương</p><p>Mua combo từ 200 chương giảm 10%, mua từ 500 chương giảm 15%, đặc biệt mua', 'nganlanxinloiem', 0, 1),
(26, 'Kiếm Đạo Đệ Nhất Tiên', 'kiem-dao-de-nhat-tien', 'https://cdn.tienvuc.xyz/media/books/kiem-dao-de-nhat-tien-bia-dung-da1d9c.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Tiêu Cẩn Du', '<p>Tô Dịch từng ngao du chu hư chư thiên, kiếm áp tinh không, độc đoán đại giới; từng chinh chiến hoàn vũ, bá tuyệt một thời đại; từng được tôn sùng là “Vạn đạo chi sư” duy nhất của Đại Hoang', 'huntercd', 0, 0),
(27, 'Siêu Cấp Thần Cơ Nhân (Bản Dịch)', 'sieu-cap-than-co-nhan-ban-dich', 'https://cdn.tienvuc.xyz/media/books/sieu-cap-than-co-nhan-9d0b.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Thập Nhị Dực Hắc Ám Sí Thiên Sứ', '<blockquote><p>Giảm 5% khi mua tối thiểu 500 chương</p><p>Giảm 10% khi mua tối thiểu 1000 chương</p><p>Giảm 20% khi mua tối thiểu 2000 chương</p><p>Giảm 30% khi mua tối thiểu 3000 chương</p><', 'mocmeo', 0, 1),
(28, 'Dạ Đích Mệnh Danh Thuật (Bản Dịch)', 'dat-ten-cho-bong-dem', 'https://cdn.tienvuc.xyz/media/books/dat-ten-cho-bong-dem-39bd.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Hội Thuyết Thoại Đích Trửu Tử', '<p>Khuyến mãi:</p><p>500 chương giảm 5%.</p><p>1.000 chương giảm 10%.</p><p>1.500 chương giảm 15%.</p><p>2.000 chương giảm 20%</p><p>Đây là câu chuyện của Khánh Trần, một đứa nhỏ 17 tuổi bất ', 'huntercd', 0, 1),
(29, 'Vạn Cổ Đệ Nhất Tông (Bản Dịch-Full)', 'van-co-de-nhat-tong-ban-dich', 'https://cdn.tienvuc.xyz/media/books/199196469-2007076466109540-1236700461247229783-n-b867.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Giang Hồ Tái Kiến', '<p></p><blockquote><p>Giảm 10% khi mua tối thiểu 500 chương</p><p>Giảm 15% khi mua tối thiểu 1200 chương</p><p>Giảm 20% khi mua tối thiểu 1700 chương</p></blockquote><p>Người nào bị cho là ph', 'mocmeo', 0, 0),
(30, 'Ta Thiên Tài Như Vậy Vì Sao Còn Muốn Thu Đồ Đệ', 'ta-thien-tai-nhu-vay-vi-sao-con-muon-thu-do-de', 'https://cdn.tienvuc.xyz/media/books/ta-thien-tai-nhu-vay-c718.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Mộc Hạ Sinh Hỏa', '<blockquote><ul><li><p><strong>Giảm giá 10% khi mua tối thiểu 200 chương</strong></p></li><li><p><strong>Giảm giá 15% khi mua tối thiểu 400 chương</strong></p></li></ul></blockquote><p>Lục Ti', 'aqua01', 0, 0),
(31, 'Thiên Cơ Lâu: Bắt đầu chế tạo Âm Hiểm Bảng ( Bản Dịch )', 'thien-co-lau-bat-dau-che-tao-am-hiem-bang-ban-dich', 'https://cdn.tienvuc.vip/media/books/bia-thien-co-lau-db64.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Hồng Hoang Long Đế', '<blockquote><p>Mua combo từ 200c giảm giá 10%,</p><p>Từ 500c giảm giá 15</p><p>Từ 1000c giảm giá 25%</p><p>Cảm ơn các đạo hữu đã ủng hộ</p></blockquote><p>Đây là một thế giới mà các triều đại', 'nganlanxinloiem', 0, 1),
(32, 'Thì Ra Ta Là Tuyệt Thế Võ Thần', 'thi-ra-ta-la-tuyet-the-vo-than', 'https://cdn.tienvuc.xyz/media/books/khong-co-tieu-de149-20210603164114-839b.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Phong Lăng Bắc', '<p>Mua 500 chương giảm 15%. 1000 chương giảm 20% nhaaaaaaaa</p><p></p><p>Dịch Phong xuyên qua thế giới tu tiên, nhưng đáng tiếc hắn chỉ là một phàm nhân, cuộc sống bình thường không có gì đán', 'snail', 0, 0),
(33, 'Phần Mềm Treo Máy: Ta Bất Tri Bất Giác Liền Vô Địch (Bản Dịch)', 'phan-mem-treo-may-ta-bat-tri-bat-giac-lien-vo-dich-ban-dich', 'https://cdn.tienvuc.xyz/media/books/z2980052448377-cec878566e8ee49986292ae6fbee76e9-f857.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Lục Thu', '<blockquote><p><strong>Giảm giá 10% khi mua tối thiểu 500c</strong></p></blockquote><p>Tề Minh ngủ một giấc dậy thì phát hiện bản thân đã xuyên không, trở thành đệ tử tạp dịch ở Thanh Vân Pho', 'mocmeo', 0, 0),
(34, 'Ta Xây Gia Viên Trên Lưng Huyền Vũ (Dịch)', 'ta-xay-gia-vien-tren-lung-huyen-vu', 'https://cdn.tienvuc.xyz/media/books/ta-xay-gia-vien-tren-lung-huyen-vu-c545.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Các Chủng Khống', '<p>Mục Lương xuyên không đến một thế giới song song tên là&nbsp;Mạt Nhật, thế giới này đã trở thành vùng đất chết, khó trồng trọt, nước mưa đều bị nhiễm axit, bầu trời tràn ngập bụi bặm.</p><', 'vankhinhvota', 0, 0),
(35, 'Tả Đạo Khuynh Thiên(Bản Dịch)', 'ta-dao-khuynh-thien-ban-dich', 'https://cdn.tienvuc.xyz/media/books/z2985548124546-23c7b2ed267b6782b22d5d4634c6b71c-2cbe.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Phong Lăng Thiên Hạ', '<blockquote><p>Giảm 10% khi mua 500c</p><p>Giảm 15% khi mua 1000c</p><p>Giảm 20% khi mua từ 2000c</p></blockquote><p>Truyện của team Thánh Thiên Tiên Vực.</p><p>Main tham tài - sợ chết - tiện', 'mocmeo', 0, 0),
(36, 'Ta! Thành Lập Tổ Chức Sát Thủ Ở Thế Giới Huyền Huyễn', 'ta-thanh-lap-to-chuc-sat-thu-o-the-gioi-huyen-huyen', 'https://cdn.tienvuc.xyz/media/books/z2993005456103-5c1bfd05e1b119374a8d882f6d8be61a-14610.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Vạn Tộc Chi Thần', '<p>Khuyến mại 10% khi mua từ 500 chương</p><p>Dạ Mệnh không cẩn thận thức đêm đột tử, xuyên đến thế giới huyền huyễn đầy rẫy nguy cơ bốn phía.</p><p>Trong lúc hắn chuẩn bị làm thế nào để giữ ', 'snail', 0, 1),
(37, 'Tuyệt Thế Dược Thần', 'tuyet-the-duoc-than', 'https://cdn.tienvuc.xyz/media/books/khong-co-tieu-de138-20210530200247-d9ce.jpeg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Phong Nhất Săc', '<p>Mua 500 chương giảm 15%. 1000 chương giảm 25%, 1500 chương giảm 35% nhaaaaaa. ^^</p><p></p><p>Một đời đan đế bị đại sư huynh hãm hại.<br><br>Từ nay, trên đời thiếu một thiên tài Thanh Vân ', 'snail', 0, 0),
(38, 'Nhân Tộc Trấn Thủ Sứ (Bản Dịch)', 'nhan-toc-tran-thu-su-ban-dich', 'https://cdn.tienvuc.xyz/media/books/274635618-1021333738419177-454560750659204515-n-d4cc.jpg', '', '2022-06-30 14:15:43.060', '2022-06-30 14:15:43.060', 'Bạch Câu Dịch Thệ', '<p>Khuyến mãi:</p><p>500 chương giảm 5%.</p><p>1.000 chương giảm 10%.</p><p>1.500 chương giảm 15%.</p><p>2.000 chương giảm 20%</p><p>Sống lại một kiếp, Thẩm Trường Thanh trở thành một thành v', 'huntercd', 0, 0),
(39, 'Bị Đánh Liền Có Thể Mạnh Lên (Dịch)', 'bi-danh-lien-manh', 'https://cdn.tienvuc.xyz/media/books/d0b1718e2f5fe501bc4e-fa1f.jpg', '', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Tân Phong', '<blockquote><p>Mua 100 chương giảm <strong>10</strong>%, 200 chương giảm <strong>20</strong>%</p><p>51-150: 4xu</p><p>150 trở đi: 5xu</p></blockquote><p><strong>#HàiHước #NhiệtHuyết #TácĐạiTh', 'nvm1997', 0, 0),
(40, 'Sổ Tay Công Lược Nữ Thần', 'so-tay-cong-luoc-nu-than', 'https://cdn.tienvuc.xyz/media/books/conluocnuthanf-32bd58.jpg', '', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Mạt Thế Ngũ Bách Ác Nhân', '<p>Lý Bân có một hệ thống công lược nữ thần!</p><p>Tác dụng có thể giúp Lý Bân phân tích tâm sinh lý của các nữ thần.</p><p>Mỗi khi hoàn thành nhiệm vụ sẽ tặng chọn phần thường cho mình.</p><', 'honde', 0, 1),
(41, 'Khoa Kỹ Luyện Khí Sư (Dịch)', 'khoa-ky-luyen-khi-su', 'https://cdn.tienvuc.xyz/media/books/a-7dc4.png', '', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Yêu Tuyên', '<p>Đây là câu chuyện về một thằng nhóc xuyên từ địa cầu sang, mang theo Trái tim Khoa Học Kỹ Thuật bái nhập vào một tông môn luyện khí.</p><p>Thằng này luyện khí không cần búa, không cần nhóm', 'daiduong28', 0, 0),
(44, 'Bắt Đầu Quá Mạnh Làm Sao Bây Giờ (Dịch)', 'bat-dau-qua-manh-lam-sao-bay-gio-dich', 'https://cdn.tienvuc.xyz/media/books/1490a227-5615-41c8-a37c-ac8835d9bba3-a51a85.jpeg', '', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Phạm Thần', '<p>Diệp Trường Sinh xuyên không về thế giới huyền huyễn, bắt đầu đánh thức kiếm thể hỗn độn, lại có được hệ thống đánh dấu. Đánh dấu Kiếm Mộ, có được Tru Tiên kiếm trận. Đánh dấu Thiên Sơn, c', 'snail', 0, 0),
(49, 'Cái Này Tử Trạch Đột Nhiên Vô Địch (Dịch)', 'cai-nay-tu-trach-dot-nhien-vo-dich-dich', 'https://cdn.tienvuc.xyz/media/books/cai-nay-tu-trach-dot-nhien-vo-dich-0f8ce4.jpg', '', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Mục Dương Sinh', '', 'snail', 0, 0),
(51, 'Trạm Thu Nhận Tai Ách (Dịch)', 'tram-thu-nhan-tai-ach-dich', 'https://cdn.tienvuc.xyz/media/books/tram-yy-3-c9562c.jpg', '', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Huyễn Mộng Liệp Nhân', '<p>Thám tử <em>\'bình thường\'</em> Ôn Văn tự cảm thấy đầu óc mình có bệnh, sau khi từ bệnh viện tâm thần tỉnh lại thì phát hiện tay phải của mình liên kết với một không gian thần bí tên là <em', 'mandaladlt', 0, 1),
(52, 'Vũ Trụ Chức Nghiệp Tuyển Thủ', 'vu-tru-chuc-nghiep-tuyen-thu', 'https://cdn.tienvuc.xyz/media/books/vu-tru-chuc-nghiep-tuyen-thu-25100.jpg', '', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Ngã Cật Tây Hồng Thị', '<p>Năm 2036, lần đầu tiên nhân loại bước lên sao Hỏa.</p><p>Năm 2052, giải thi đấu Võ đạo thế giới năm đầu tiên cử hành, đây cũng là cuộc thi đối kháng tối cao của toàn cầu, cả thế giới trở n', 'aqua01', 0, 0),
(53, 'Linh Cảnh Hành Giả', 'linh-canh-hanh-gia', 'https://cdn.tienvuc.xyz/media/books/linh-canh-hanh-gia-d7e9a9.jpg', '', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Mại Báo Tiểu Lang Quân', '<p>Tuyên cổ thông kim, đều có lời đồn đời nào cũng có Linh cảnh.</p><p>Những cách nói có liên quan tới Linh cảnh, danh nhân nhã sĩ các triều đại có nhắc tới rất nhiều, trong thơ cũng có ghi l', 'huntercd', 0, 0),
(54, 'Bất Tử Ta Thật Sự Là Quá Mạnh (Dịch)', 'bat-tu-ta-that-su-la-qua-manh-dich', 'https://cdn.tienvuc.xyz/media/books/ae2412e6-6607-4291-8575-7bdc486623a4-936da4.jpeg', 'full', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Đường Tào Lão Sư', '<p>Kẻ thù đánh vào ngực ngươi một chưởng, nội tạng ngươi vỡ nát, trong nháy mắt thân thể ngươi hồi phục, đồng thời trở nên càng mạnh hơn.</p><p>Buổi sáng ngươi rời giường, đánh một lần “Thất ', 'snail', 0, 1),
(55, 'Toàn Bộ Công Pháp Của Ta Đều Nhờ Nhặt (Dịch)', 'toan-bo-cong-phap-cua-ta-deu-nho-nhat-dich', 'https://cdn.tienvuc.xyz/media/books/z2954338388268-d7d106da77846b69b6b78d7e94c1b111-3ecc.jpg', '', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Bách Vạn Đan Cơ Vương', '<blockquote><p>Mua 100 chương giảm&nbsp;<strong>10</strong>%, 200 chương giảm&nbsp;<strong>20</strong>%</p><p>5xu/chương</p></blockquote><p>【Kí chủ nhặt được thiên phú chí cường:&nbsp;Thí Thầ', 'nvm1997', 0, 0),
(56, 'Thiên Đạo Bảng Hiện Thế: Ta Thiên Đế Tư Chất Bị Lộ Ra (Bản Dịch)', 'thien-dao-bang-hien-the-ta-thien-de-tu-chat-bi-lo-ra-ban-dich', 'https://cdn.tienvuc.xyz/media/books/tu-chat-lo-ra-93ced2.jpg', 'full', '2022-06-30 14:16:20.350', '2022-06-30 14:16:20.350', 'Ám Điểm', '<p><strong>Văn án</strong></p><p>Vương Ương xuyên đến thế giới huyền huyễn, trở thành Thánh Tử Vương gia của Chân Võ đại lục Cửu Đại Nhân tộc và đồng thời thức tỉnh”Hệ thống nâng cấp tự động”', 'luizy97', 0, 1),
(57, 'Xích Tâm Tuần Thiên (Dịch)', 'xich-tam-tuan-thien-dich', 'https://cdn.tienvuc.vip/media/books/xich-tam-tuan-thien-baf7.jpg', 'full', '2022-09-17 19:00:00.000', '2022-09-17 19:00:00.000', 'Đang cập nhật', '<div data-v-ca1d7a7c=\"\" class=\"\"><blockquote><p><strong><u>Khuyến mãi:</u></strong></p><p><strong>Giảm 10% khi mua từ 50 chương trở lên.</strong></p><p><strong>Giảm 20% khi mua từ 300 chương ', 'Đang cập nhật', 0, 1);
